import React, { useState, useEffect } from "react";

function DataFetching() {
    const [posts, setPosts] = useState([]);
    const [postId, setPostId] = useState([]);
    const [title, setTitle] = useState("");
    const [text, setText] = useState("");
    /*useEffect(() => {
        /*async function getResponse() {
            let response = await fetch('https://jsonplaceholder.typicode.com/posts')
            let content = await response.json()
            content = content.splice(0, 4)
            setPosts(response.data)
            let list = document.querySelector('.posts')

            let key;

            for (key in content) {

                list.innerHTML += `
                <li>
                  <h4>${content[key].title}</h4>
                  <p>${content[key].body}</p>
                </li>
              `
            }
        };

          getResponse();
    }, [])*/

    useEffect(() => {
        const abortCont = new AbortController();

        setTimeout(() => {
            async function getResponse() {
                let response = await fetch('http://localhost:8000/Posts', { signal: abortCont.signal })
                let content = await response.json();
                //content = content.splice(96, content.length)
                setPosts(content)
                console.log(content, "its posts")
            };

            getResponse();
        }, 100);

        return () => abortCont.abort();
    }, ['http://localhost:8000/Posts'])

    //post method

    /*useEffect(() => {
        // POST request using fetch inside useEffect React hook
        const requestOptions = {
            method: 'POST',
            headers: { 'Content-Type': 'application/json' },
            body: JSON.stringify({
                 title: title,
                 body : text
                })
        };
        fetch('https://jsonplaceholder.typicode.com/posts', requestOptions)
            .then(response => response.json())
            .then(data => setPostId(data.id));

    // empty dependency array means this effect will only run once (like componentDidMount in classes)
    }, []);*/

    //handle
    const handleInputTitleChange = (e) => {
        setTitle(e.target.value)
    }

    const handleInputTextChange = (e) => {
        setText(e.target.value)
    }

    const handleSubmit = (e) => {
        e.preventDefault();

        const Blog = {title, text}
        console.log(Blog)

        // POST request using fetch inside useEffect React hook
        const requestOptions = {
            method: 'POST',
            headers: { 'Content-Type': 'application/json' },
            body: JSON.stringify(Blog)
        };
        fetch('http://localhost:8000/Posts', requestOptions)
            .then(response => response.json())
            //.then(data => setPostId(data.id))
            .then(post => {
                posts.push(post)
            })
            .then(() => {
                console.log(posts, "post added")

            })

    }

    return (
        <div>
            <div>
                <form onSubmit={handleSubmit}>
                    <label>Blog title:</label>
                    <input onChange={e => handleInputTitleChange(e)} id="title-value" value={title}></input>
                    <label>Blog text:</label>
                    <textarea onChange={e => handleInputTextChange(e)} type="text" id="text-value" value={text}></textarea>
                    <button type="submit">Add Blog{postId}</button>
                </form>
                {posts.map(post => (
                    <div key={post.id}>
                        <h4>{post.title}</h4>
                        <p>{post.body}</p>
                    </div>
                ))}
            </div>
        </div>
    )
}

export default DataFetching;
