import { useState, useEffect, useReducer } from "react";


function reducer (state, action){
  switch(action.type){
    case 'title':  
      return { 
          ...state,
          title: action.payload
      }
    case 'body':  
      return { 
          ...state,
          body: action.payload
      }
    default:
      return state
  }
}


function NewPost() {
  //const newPosts = props.newPosts
  //<NewPost handleSubmit={handleSubmit} handleInputTitleChange={handleInputTitleChange} handleInputTextChange={handleInputTextChange} title={title} body={body}/>


  //useState
  //const [newPosts, setNewPosts] = useState([]);
  /*const [title, setTitle] = useState("");
  const [body, setBody] = useState("");*/

  //useReducer
  const [state, dispatch] = useReducer(reducer, { title: "", body: ""})

  const handleInputTitleChange = (e) => {
    //setTitle(e.target.value)
    dispatch({ payload: content})
}

  const handleInputTextChange = (e) => {
      //setBody(e.target.value)
  }

  const handleSubmit = (e) => {
      e.preventDefault();

      const Blog = {title, body}
      console.log(Blog)

      // POST request using fetch inside useEffect React hook
      const requestOptions = {
          method: 'POST',
          headers: { 'Content-Type': 'application/json' },
          body: JSON.stringify({
              title: title,
              body : body
              })
      };
      fetch('http://localhost:8000/Posts', requestOptions)
          .then(response => {
            response.json()
            console.log(response, "post added")
          })
          //.then(data => setPostId(data.id))
          //.then(post => newPosts.push(post))
          .then(() => {
              console.log("post added")
          })
  }

  /*{newPosts.map(newPost => (
    <div key={newPost.id}>
        <h4>{newPost.title}</h4>
        <p>{newPost.body}</p>
    </div>
))}*/

  return (
    <div className="form-container">
      <h1></h1>
      <form onSubmit={handleSubmit}>
        <label>Blog title:</label>
        <input onChange={e => handleInputTitleChange(e)} id="title-value" type="text" value={title}></input>
        <label>Blog text:</label>
        <textarea onChange={e => handleInputTextChange(e)} type="text" id="text-value" value={body}></textarea>
        <button type="submit">Add Blog</button>
      </form>
    </div>
  )

}

export default NewPost;
