  const [editForm, setEditForm] = useState("edit-form");

  return (
    <div className='post-list'>
      {posts.map(post => (
        <div key={post.id} className="post-list-item">
          <img src={myPix[post.id]} />
          <h4>{post.title}</h4>
          <p>{post.body}</p>
