import { useEffect, useReducer, useState } from "react";
import PostList from "./PostList";


function reducer (state, action){
    return { 
        ...state,
        posts: action.payload
    }
}



function DataFetching() {
    const [state, dispatch] = useReducer(reducer, { posts: []});
    const [length, setLength] = useState("100");

    useEffect(() => {
        const abortCont = new AbortController();

        setTimeout(() => {
            async function getResponse() {
                let response = await fetch('https://jsonplaceholder.typicode.com/posts', { signal: abortCont.signal })
                let content = await response.json();
                content = content.splice(98, length)
                dispatch({ payload: content})
                console.log(content, "its posts")
            };

            getResponse();
        }, 100);

        return () => abortCont.abort();
    }, [])


    const requestOptions = {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({
            userId: 10,
            title: state.title,
            body : state.body
            })
    };
    fetch('https://jsonplaceholder.typicode.com/posts', requestOptions)
        .then(response => {
          response.json()
          console.log(response, "request sucsess")
        })
        .then((data) => {
            //dispatch({ payload: })
            //setPosts((posts) => [...posts,data]);
            setLength("101")
            console.log(state.posts,"post added")
        })


    return (
        <>
            <PostList posts ={state.posts}/>
        </>
    )
}

export default DataFetching;
